import React, { useState, useEffect } from 'react';
import Table from 'react-bootstrap/Table';
import { Container } from './styles';

export default function Grid(props) {
  const { list } = props;
  const { header } = props;

  const [searchTerm, setSearchTerm] = useState('');
  // const [searchResults, setSearchResults] = useState([]);
  console.log(list);

  useEffect(() => {
    const results = list.filter(list =>
      list.name.toLowerCase().includes(searchTerm)
    );
    setSearchResults(results);
  }, [searchTerm]);

  if (header === undefined || list === undefined) {
    return <h1>Vazio</h1>;
  }

  function handleChange(e) {
    setSearchTerm(e.value);
  }

  return (
    <Container>
      <input
        type="text"
        placeholder="Search"
        value={searchTerm}
        onChange={handleChange}
      />
      <Table>
        <thead>
          <tr>
            {header.map(function(e) {
              return <th key={e.name}>{e.name}</th>;
            })}
            <th>Ações</th>
          </tr>
        </thead>
        <tbody>
          {list.map(function(item) {
            const newObj = Object.values(item);
            return (
              <tr key={item.id}>
                {newObj.map(function(m, index) {
                  if (header[index].type === 'img') {
                    return (
                      <td key={m}>
                        <img
                          src="https://api.adorable.io/avatars/50/abott@adorable.png"
                          alt="Avatar"
                        />
                      </td>
                    );
                  }
                  return <td key={m}>{m}</td>;
                })}
                <td>
                  <button>Opcoes</button>
                </td>
              </tr>
            );
          })}
        </tbody>
      </Table>
    </Container>
  );
}
