import React, { useState, useEffect } from 'react';
import { Form, Input } from '@rocketseat/unform';
import { Container } from './styles';
import Img from '../../../components/Img';
import api from '../../../services/api';

export default function Courier_Edit(props) {
  const { id } = props.match.params;
  const [courier, setCourier] = useState([]);

  useEffect(() => {
    async function loadCourier() {
      const response = await api.get(`courier/${id}`);
      const { data } = response;
      setCourier(data);
    }
    loadCourier();
  }, []);

  function handleSubmit(data) {
    console.log(courier);

    // const response = await api.post('courier', data);
  }

  return (
    <Container>
      <Form onSubmit={handleSubmit}>
        <Img name="avatar_id" />
        <label>Nome</label>
        <Input name="name" value={courier.name} />
        <label>E-mail</label>
        <Input name="email" value={courier.email} />
        <button type="submit">Enviar</button>
      </Form>
    </Container>
  );
}
